import bcrypt from 'bcryptjs';
import models from '../models';
import token from '../services/token';

export default {
  register: async (req, res) => {
    try {
      req.body.password = await bcrypt.hashSync(req.body.password, 10);
      const user = await models.User.create(req.body);
      res.status(200).json(user);
    } catch (error) {
      res.status(500).send({
        message: 'OcurriÃ³ un error'
      });
      console.log('error', error);
      // res.status(500).send({
    }
  },

  register_admin: async (req, res) => {
    try {
      // Forzamos el rol a "admin" sin depender del cliente
      req.body.rol = 'admin';

      // Encriptamos la contraseÃ±a de forma asincrÃ³nica
      req.body.password = await bcrypt.hash(req.body.password, 10);

      // Creamos el usuario en la base de datos
      const user = await models.User.create(req.body);

      // Respondemos con Ã©xito
      res.status(200).json(user);
    } catch (error) {
      // Captura de errores
      res.status(500).send({
        message: 'OcurriÃ³ un error al registrar el admin'
      });
      console.log('Error en register_admin:', error);
    }
  },

  login: async (req, res) => {
    try {
      const user = await models.User.findOne({
        email: req.body.email,
        state: 1
      });
      if (user) {
        //Si estÃ¡ registrado en el sistema
        let compare = await bcrypt.compare(req.body.password, user.password);
        if (compare) {
          let tokenT = await token.encode(user._id, user.rol, user.email);

          const USER_FRONTEND = {
            token: tokenT,
            user: {
              name: user.name,
              email: user.email,
              surname: user.suretName,
