        res.status(500).send({
          message: 'El usuario no existe'
        });
      }
    } catch (error) {
      res.status(500).send({
        message: 'OcurriÃ³ un error'
      });
    }
  },
  login_admin: async (req, res) => {
    try {
      const user = await models.User.findOne({
        email: req.body.email,
        state: 1,
        rol: 'admin'
      });

      if (user) {
        // Si estÃ¡ registrado en el sistema
        let compare = await bcrypt.compare(req.body.password, user.password);
        if (compare) {
          console.log('USER COMPLETO QUE VIENE DE BD:', user); // ð aquÃ­ sÃ­ se imprime

          let tokenT = await token.encode(user._id, user.rol, user.email);

          const USER_FRONTEND = {
            token: tokenT,
            user: {
              name: user.name,
              email: user.email,
              surname: user.suretName,
              avatar: user.avatar,
              rol: user.rol
            }
          };

          res.status(200).json(USER_FRONTEND);
        } else {
          res.status(500).send({
            message: 'El usuario no existe'
          });
        }
      } else {
        res.status(500).send({
          message: 'El usuario no existe'
        });
      }
    } catch (error) {
      res.status(500).send({
        message: 'OcurriÃ³ un error'
      });
    }
  },

  update: async (req, res) => {
    try {
      if (req.files) {
        var img_path = req.files.avatar.path;
        var name = img_path.split('\\');
        var avatar_name = name[2];
        console.log('avatar_name', avatar_name);
      }
      if (req.body.password) {
        req.body.password = await bcrypt.hash(req.body.password, 10);
      }
      const UserT = await models.User.findByIdAndUpdate(
        { _id: req.body._id },
        req.body
      );
      res.status(200).json({
        message: 'El usuario se actualizÃ³ correctamente',
        user: UserT
      });
    } catch (error) {
      res.status(500).send({
        message: 'OcurriÃ³ un error'
      });
    }
  },
  list: async (req, res) => {
    try {
      const users = await models.User.find().sort({ createdAt: -1 });
      res.status(200).json({ users });
    } catch (error) {
      res.status(500).send({ message: 'OcurriÃ³ un error al listar usuarios' });
      console.log('Error en list:', error);
    }
  },
  remove: async (req, res) => {
    try {
      const User = await models.User.findByIdAndDelete({ _id: req.body._id });
      res.status(200).json({
        message: 'El usuario se eliminÃ³ correctamente',
        User
      });
    } catch (error) {
      res.status(500).send({
        message: 'OcurriÃ³ un error'
      });
    }
  }
};

